GO
CREATE LOGIN [ContosoUser] WITH PASSWORD=N'Contoso123!', DEFAULT_DATABASE=[master], CHECK_EXPIRATION=OFF, CHECK_POLICY=OFF
GO
ALTER SERVER ROLE [sysadmin] ADD MEMBER [ContosoUser]
GO


CREATE DATABASE Contoso
GO

USE Contoso
GO

CREATE TABLE [dbo].[News] (
    [ItemID]     [int] IDENTITY (1000, 1) NOT NULL,
    [CategoryID] [int]                    NOT NULL,
    [Title]      [varchar] (128)          NOT NULL,
    [Subtitle]   [varchar] (512)          NOT NULL,
    [BodyText]   [text]                   NOT NULL
) ON [PRIMARY]
GO

ALTER TABLE [dbo].[News] ADD 
    CONSTRAINT [PK_News] PRIMARY KEY CLUSTERED 
    (
        [ItemID]
    )  ON [PRIMARY] 
GO

