CREATE PROCEDURE proc_GetNewsItem @ItemID int AS
    SELECT [ItemID], [CategoryID], [Title], [Subtitle], [BodyText] FROM [News] WHERE [ItemID]=@ItemID
GO

CREATE PROCEDURE proc_GetNewsItemCategory @ItemID int AS
    SELECT [CategoryID] FROM [News] WHERE [ItemID]=@ItemID
GO

CREATE PROCEDURE proc_GetNewsItemCategoryAndTitle @ItemID int AS
    SELECT [CategoryID], [Title] FROM [News] WHERE [ItemID]=@ItemID
GO

CREATE PROCEDURE proc_GetNewsItemTitleAndBodyText @ItemID int AS
    SELECT [Title], [BodyText] FROM [News] WHERE [ItemID]=@ItemID
GO

CREATE PROCEDURE proc_GetAllNewsItemsInCategory @CategoryID int AS
    SELECT [ItemID], [Title], [BodyText] FROM [News] WHERE [CategoryID]=@CategoryID ORDER BY [ItemID]
GO

CREATE PROCEDURE proc_GetAllNewsItems AS
    SELECT [ItemID], [CategoryID], [Title] FROM [News] ORDER BY [CategoryID], [ItemID]
GO

CREATE PROCEDURE proc_DeleteNewsItem @ItemID int AS
    DELETE FROM [News] WHERE [ItemID]=@ItemID
GO

CREATE PROCEDURE proc_UpdateNewsItem @ItemID int, @CategoryID int, @Title varchar (128), @Subtitle varchar (512), @BodyText text AS
    UPDATE [News] SET [CategoryID]=@CategoryID, [Title]=@Title, [Subtitle]=@Subtitle, [BodyText]=@BodyText WHERE [ItemID]=@ItemID
GO

